function parseXml(t){$("#main").html("<ul id='content' data-role='listview' data-inset='true'></ul>"),$(t).find("MovieListItem").each(function(){$("#content").append("<a href='"+$(this).find("Ref").text()+"'><li>"+$(this).find("ID").text()+"<h3>"+$(this).find("Name").text()+"</h3><p>"+$(this).find("Ref").text()+"</h2></li></a>")})}$(document).ready(function(){$.ajax({type:"GET",url:"dfiList.xml",dataType:"xml",success:parseXml})});